import React,{useState} from "react";

const StateTutorial = () => {

    const [counter,setCounter] = useState(0);

    function increase (){
        setCounter(counter+1)
    }

    function decrease (){
        setCounter(counter-1)
    }

    return(
        <div>

            <button onClick={increase}>Increment</button>
            <p>{counter}</p>
            <button onClick={decrease}>Decrement</button>
        </div>
    )
}

export default StateTutorial;
