const MemoTutorial = () => {

const data = FetchAPI("https://fakestoreapi.com/products");

const findLargeTitle = ()=>{

    if(!data) return "No Data";

    let largestName = findLargeTitle();


    for(let i =0; i< data.length; i++){
        if(data[i].title.length > largestName ){
            largestName = data[i].title
        }
    }

    console.log("Function Excecuted")

    return largestName


}



return(
    <div className="container">
            <div className="box">
                 <p>{largestName}</p>
            </div>
    </div>
)
}

export default MemoTutorial;
